using System.Collections;using System.Collections.Generic;using UnityEngine;public class createObj : MonoBehaviour{    public GameObject objPrefab;    private SteamVR_TrackedObject trackedObj;    private SteamVR_Controller.Device Controller    {        get { return SteamVR_Controller.Input((int)trackedObj.index); }    }    void Awake()    {        trackedObj = GetComponent<SteamVR_TrackedObject>();    }    void Update()    {        if (Controller.GetPressDown(SteamVR_Controller.ButtonMask.Grip))        {            Instantiate(objPrefab, new Vector3(0, 0, 0), Quaternion.identity);        }    }}